let table=document.querySelector(".product-list");table.onmouseover=function(e){let t=e.target.closest("li");t&&t.querySelector(".product-item-overlay").classList.remove("visually-hidden")},table.onmouseout=function(e){let t=e.target.closest("li");t&&t.querySelector(".product-item-overlay").classList.add("visually-hidden")};